package variableType;

public class Example01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte somiBye = 1;
		System.out.println(somiBye);
		
		short somiShort = 2;
		System.out.println(somiShort);
		
		int somiInt = 3;
		System.out.println(somiInt);
		
		long somiLong = 4L;// long 데이터타입은 숫자 옆에 L을 기재해주어야 한다.
		System.out.println(somiLong);
		
		boolean somiBool = false;
		System.out.println(somiBool);
		
		char somiChar = 'a'; //여러 개의 문자열은 "" <- 쌍따옴표로 묶고, 문자 하나는 ''로 묶는다.
		System.out.println(somiChar);
		
		float somiFloat = 3.14f; // float도 옆에 f를 붙여준다
		System.out.println(somiFloat);
		
		double somiDouble = 3.14;
		System.out.println(somiDouble);
	}

}
