package supplement;

public class Supplement2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//자주 사용하는 Escape문자 모음
		// \n : 줄바꿈
		// \t : 탭
		// \" : 큰 따옴표
		// \' : 작은 따옴표
		// \\ : 역슬래쉬
		
		System.out.println("개\n행");
		System.out.println("ㅌ\tㅐ\tㅂ");
		System.out.println("\'따옴표\'");
		System.out.println("\\");
	}

}
